Get-ADUser -SearchBase 'OU=SY-Brugere, dc=ad, dc=bollers, dc=dk' -filter {Enabled -eq $True -and PasswordNeverExpires -eq $False} –Properties "samAccountName", "msDS-UserPasswordExpiryTimeComputed" | Select-Object -Property "samAccountName",@{Name="ExpiryDate";Expression={[datetime]::FromFileTime($_."msDS-UserPasswordExpiryTimeComputed").ToString('yyyy-MM-dd')}} #| Export-Csv -Path .\Documents\UsersExpiry.csv -force -NoTypeInformation
